input_sequence<-""
recognition_site_bsai<-"GGTCTC"
mutations<-c(137,143,147,232,234)

test_that("Recognition of missing sequence end (codons) works ", {
  expect_that(msd_mutate(input_sequence, prefix="TT" ,restriction_enzyme=recognition_site_bsai, suffix="A", vector=c("AATG", "AAGC"), replacements=mutations, replacement_range=5, binding_min_length=4 ,primer_length=9, target_temp=60, fragment_min_size=60 )
, throws_error("No stop codon in the provided sequence. Stopping here. Please check the provided sequence!"))
})

input_sequence<-""

test_that("Detection of faulty reading frame works ", {
  expect_that(msd_mutate(input_sequence, prefix="TT" ,restriction_enzyme=recognition_site_bsai, suffix="A", vector=c("AATG", "AAGC"), replacements=mutations, replacement_range=5, binding_min_length=4 ,primer_length=9, target_temp=60, fragment_min_size=60 )
              , throws_error("The length of the sequence is no factor of 3. Please check your sequence. The length of the sequence was: 901"))
})

input_sequence<-""

test_that("Detection of missing start codon works ", {
  expect_that(msd_mutate(input_sequence, prefix="TT" ,restriction_enzyme=recognition_site_bsai, suffix="A", vector=c("AATG", "AAGC"), replacements=mutations, replacement_range=5, binding_min_length=4 ,primer_length=9, target_temp=60, fragment_min_size=60 )
              , throws_error("No Methionine in the provided sequence. Stopping here. Please check the provided sequence."))
})

input_sequence<-""

test_that("Detection of missing start codon and shifting works ", {
  expect_that(msd_mutate(input_sequence, prefix="TT" ,restriction_enzyme=recognition_site_bsai, suffix="A", vector=c("AATG", "AAGC"), replacements=mutations, replacement_range=5, binding_min_length=4 ,primer_length=9, target_temp=60, fragment_min_size=60 )
              , gives_warning("No Methionine at first codon found! Please check the provided sequence! Took codon # 5 as start."))
})

